import React, { useState } from 'react'
import Input from './components/Input'
import List from './components/List'
import Filter from './components/Filter'
import './App.css'


export default function App() {
    const [lists, setList] = useState([
        {
            id: 0,
            text: '吃饭',
            done: true,
            show: true
        },
        {
            id: 1,
            text: '睡觉',
            done: false,
            show: true
        },
        {
            id: 2,
            text: '打游戏',
            done: true,
            show: true
        }
    ])


    function changeList(newList) {
        setList(newList)
    }


    function addList(list) {
        const newList = [...lists, list]
        changeList(newList)
    }


    function deleteList(id) {
        const newList = lists.filter(list => list.id !== id)
        changeList(newList)
    }


    function startEditList(id) {
        const newList = lists.map(list => {
            if (id === list.id) {
                list.editing = true
            }
            return list
        })
        changeList(newList)
    }

    function finishEditList(id, text) {
        const newList = lists.map(list => {
            if (list.id === id) {
                list.text = text
                list.editing = false
                return list
            } else {
                return list
            }
        })
        changeList(newList)
    }


    function completeList(id, a) {
        const newList = lists.map(list => {
            if (list.id === id)
                return { ...list, a }
            else
                return list
        })
        changeList(newList)
    }


    function completeAllList(a) {
        const newList = lists.map(list => {
            return { ...list, a }
        })
        changeList(newList)
    }


    function selectAll() {
        const newList = lists.map(list => {
            list.show = true
            return list
        })
        changeList(newList)
    }

    function selectActive() {
        const newList = lists.map(list => {
            if (list.done === true) {
                list.show = true
                return list
            } else if (list.done === false) {
                list.show = false
                return list
            } else {
                return list
            }
        })
        changeList(newList)
    }

    function selectCompleted() {
        const newList = lists.map(list => {
            if (list.done === true) {
                list.show = false
                return list
            } else if (list.done === false) {
                list.show = true
                return list
            } else {
                return list
            }
        })
        changeList(newList)
    }


    return(
        <div>
            <div className="todoapp">
                <Input lists={lists} addList={addList} completeAllLists={completeAllLists} />
                <List todos={todos} deleteList={deleteList} completeList={completeList} startEditList={startEditList} finishEditList={finishEditList} />
                <Filter todos={todos} clearAllComp={clearAllComp} seeAll={seeAll} seeActive={seeActive} seeComp={seeComp} />
            </div>
        </div>
    )
}
